package com.sun.listener;import lombok.extern.slf4j.Slf4j;import org.apache.rocketmq.spring.annotation.RocketMQMessageListener;import org.apache.rocketmq.spring.core.RocketMQListener;import org.springframework.stereotype.Component;/** * 订单超时 * RocketMQ默认延时级别，0表示不延时 * 1s 5s 10s 30s 1m 2m 3m 4m 5m 6m 7m 8m 9m 10m 20m 30m 1h 2h 1d */@Slf4j@Component@RocketMQMessageListener(topic = "${rocketmq.topic.goods}", consumerGroup = "${rocketmq.consumer.group.goods}")public class OrderListener implements RocketMQListener<String> {    @Override    public void onMessage(String message) {        log.info("------- OrderListener收到订单: {} -------", message);        System.out.println("订单未支付，自动关闭");    }}